INSERT INTO creature_text (entry, groupid, id, TEXT, TYPE, LANGUAGE, probability, emote, duration, sound, COMMENT) VALUES
(59822, 0, 0, "Oh, hello Zan, it is good of you to visit ol' Auntie Stormstout.", 12, 0, 100, 0, 0, 28528, 'Auntie Stourmstout'),
(59822, 1, 0, "Oh, Zan! You remind me so much of your father.", 12, 0, 100, 0, 0, 28529, 'Auntie Stourmstout'),
(59822, 2, 0, "It is certainly a nice day outside!", 12, 0, 100, 0, 0, 28530, 'Auntie Stourmstout'),
(59822, 3, 0, "Have you seen the size of Esme's turnips?", 12, 0, 100, 0, 0, 28531, 'Auntie Stourmstout'),
(59822, 4, 0, "Abandoned? Oh heavens no! Uncle Gao is in charge while the others are beyond the wall. Isn't that exciting?", 12, 0, 100, 0, 0, 28532, 'Auntie Stourmstout'),
(59822, 5, 0, "I have some cookies for you!", 12, 0, 100, 0, 0, 28533, 'Auntie Stourmstout');
INSERT INTO creature_text (entry, groupid, id, TEXT, TYPE, LANGUAGE, probability, emote, duration, sound, COMMENT) VALUES
(59704, 0, 0, "I am not Zan - I am Chen Stormstout!", 12, 0, 100, 0, 0, 29766, 'Chen Stourmstout'),
(59704, 1, 0, "Tell me, what has happened here?", 12, 0, 100, 0, 0, 29767, 'Chen Stourmstout'),
(59704, 2, 0, "Where are the other Stormstouts? Why are hozen all over the brewery?", 12, 0, 100, 0, 0, 29768, 'Chen Stourmstout'),
(59704, 3, 0, "Auntie Stormstout... why is the brewery abandoned?", 12, 0, 100, 0, 0, 29769, 'Chen Stourmstout'),
(59704, 4, 0, "I see - and where is Uncle Gao?", 12, 0, 100, 0, 0, 29770, 'Chen Stourmstout'),
(59704, 5, 0, "There is no time for cookies! Well, one cookie. Just one.", 12, 0, 100, 0, 0, 29771, 'Chen Stourmstout'),
(59704, 6, 0, "Wait - these are ghost cookies. These aren't filling at all!", 12, 0, 100, 0, 0, 29772, 'Chen Stourmstout');
