INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES
(71734, 0, 0, 'So foolish...', 14, 0, 100, 0, 0, 38896, 'Sha of Pride - aggro'),
(71734, 1, 0, '', 14, 0, 100, 0, 0, 38897, 'Sha of Pride - death'),
(71734, 2, 0, 'You\'ve let your pride cloud your vision, Titan puppets! You can never contain ME!', 14, 0, 100, 0, 0, 38898, 'Sha of Pride - unleashed'),
(71734, 3, 0, 'Come, face me. Give in to your pride. Show me your "Greatness".', 14, 0, 100, 0, 0, 38899, 'Sha of Pride - intro'),
(71734, 4, 0, 'That one is WEAK.', 14, 0, 100, 0, 0, 38905, 'Sha of Pride - slay 1'),
(71734, 4, 1, 'That one is unworthy of your group.', 14, 0, 100, 0, 0, 38906, 'Sha of Pride - slay 2'),
(71734, 4, 2, 'Blame your companions!', 14, 0, 100, 0, 0, 38907, 'Sha of Pride - slay 3'),
(71734, 5, 0, 'You are better than your companions!', 14, 0, 100, 0, 0, 38908, 'Sha of Pride - spell 1'),
(71734, 5, 1, 'Your arrogance compels you...', 14, 0, 100, 0, 0, 38909, 'Sha of Pride - spell 2'),
(71734, 6, 0, 'Succumb to your pride!', 14, 0, 100, 0, 0, 38910, 'Sha of Pride - spell 3'),
(71734, 7, 0, 'Your arrogance feeds me!', 14, 0, 100, 0, 0, 38911, 'Sha of Pride - spell 4'),
(71734, 6, 1, 'You should bow to no king or warchief.', 14, 0, 100, 0, 0, 38912, 'Sha of Pride - spell 5');
INSERT INTO `locales_creature_text` (`entry`,`textGroup`,`id`,`text_loc8`) VALUES
(71734, 0, 0, 'Как глупо...'),
(71734, 1, 0, ''),
(71734, 2, 0, 'Гордыня затуманила твой взор, слуга Титанов. Тебе не одолеть МЕНЯ.'),
(71734, 3, 0, 'Идите ко мне. Отдайтесь гордыне. Покажите свое "Величие".'),
(71734, 4, 0, 'Какая СЛАБОСТЬ!'),
(71734, 4, 1, 'Это было ваше слабое звено.'),
(71734, 4, 2, 'Вини своих друзей!'),
(71734, 5, 0, 'Ты лучше всех своих друзей.'),
(71734, 5, 1, 'Надменность подчинила тебя.'),
(71734, 6, 0, 'Отдайся гордыне.'),
(71734, 7, 0, 'Надменность питает меня.'),
(71734, 6, 1, 'Не кланяйтесь ни королю, ни вождю.');
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES
(71965, 0, 0, 'Be humble.', 14, 0, 100, 0, 0, 38900, 'Sha of Pride - Norushen - spell 1'),
(71965, 0, 1, 'Free yourself of arrogance.', 14, 0, 100, 0, 0, 38901, 'Sha of Pride - Norushen - spell 2'),
(71965, 1, 0, 'You... must... contain... it...', 14, 0, 100, 0, 0, 38902, 'Sha of Pride - Norushen - death'),
(71965, 2, 0, 'The corruption is amplifying. The fragments must be purged before it becomes too great.', 14, 0, 100, 0, 0, 38903, 'Sha of Pride - Norushen - event 1'),
(71965, 3, 0, 'It did not matter. It comes. Steel your hearts and prepare your souls.', 14, 0, 100, 0, 0, 38904, 'Sha of Pride - Norushen - event 2');
INSERT INTO `locales_creature_text` (`entry`,`textGroup`,`id`,`text_loc8`) VALUES
(71965, 0, 0, 'Будьте скромнее.'),
(71965, 0, 1, 'Отбросьте высокомерие.'),
(71965, 1, 0, 'Вы... должны... удержать... его...'),
(71965, 2, 0, 'Порча усиливается. Фрагменты нужно уничтожить, иначе будет поздно.'),
(71965, 3, 0, 'Не сработало. Он идет. Приготовьтесь, будьте стойки душой и сердцем.');
