INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES
(62837, 0, 0, 'Death to all who dare challenge my empire!', 14, 0, 100, 0, 0, 29283, 'Shekzeer aggro'),
(62837, 1, 0, 'Augh... ah ... I... have failed.', 14, 0, 100, 0, 0, 29268, 'Shekzeer death'),
(62837, 2, 0, 'Yes, master.', 14, 0, 100, 0, 0, 29269, 'Shekzeer destroy chamber'),
(62837, 3, 0, 'Power... I must have more power! Guards! Protect your empress!', 14, 0, 100, 0, 0, 29270, 'Shekzeer recharge 1'),
(62837, 4, 0, 'I will dispose of the outsiders.', 14, 0, 100, 0, 0, 29271, 'Shekzeer recharge 2'),
(62837, 5, 0, 'More... more! I must have more!', 14, 0, 100, 0, 0, 29272, 'Shekzeer recharge 3'),
(62837, 6, 0, 'Such is the fate of those who question authority.', 14, 0, 100, 0, 0, 29273, 'Shekzeer reset'),
(62837, 7, 0, '', 14, 0, 100, 0, 0, 29274, 'Shekzeer vizier death 1'),
(62837, 8, 0, '', 14, 0, 100, 0, 0, 29275, 'Shekzeer vizier death 2'),
(62837, 9, 0, '', 14, 0, 100, 0, 0, 29276, 'Shekzeer vizier death 3'),
(62837, 10, 0, '', 14, 0, 100, 0, 0, 29277, 'Shekzeer vizier death 4'),
(62837, 11, 0, 'Seal the gates! Bar the exits! Destroy these intruders!', 14, 0, 100, 0, 0, 29278, 'Shekzeer intro'),
(62837, 12, 0, 'Taste defeat, stranger!', 14, 0, 100, 0, 0, 29279, 'Shekzeer slay 1'),
(62837, 12, 1, 'Breathe no longer!', 14, 0, 100, 0, 0, 29280, 'Shekzeer slay 2'),
(62837, 13, 0, 'Sing... yes! Sing in agony!', 14, 0, 100, 0, 0, 29281, 'Shekzeer spell 1'),
(62837, 14, 0, 'You will serve ME now!', 14, 0, 100, 0, 0, 29282, 'Shekzeer spell 2');
INSERT INTO `locales_creature_text` (`entry`,`textGroup`,`id`,`text_loc8`) VALUES
(62837, 0, 0, 'Я убью любого, кто осмелится пойти против моей империи!'),
(62837, 1, 0, 'Я повержена!'),
(62837, 2, 0, 'Да, хозяин.'),
(62837, 3, 0, 'Власть.. Я хочу больше власти! Стража! Защищайте вашу императрицу!'),
(62837, 4, 0, 'Я избавлюсь от чужаков.'),
(62837, 5, 0, 'Еще... еще! Мне нужно больше!'),
(62837, 6, 0, 'Вот что ждет тех, кто не признает власти.'),
(62837, 7, 0, 'Мой голос исчез. Как меня услышат мои подданные? Останется ли их любовь со мной или они ополчатся на меня, как и предатели Клакси.'),
(62837, 8, 0, 'Это они послали вас. Они бы не посмели.'),
(62837, 9, 0, 'Вы за свое высокомерие вы заплатите. Мои сильнейшие воины разорвут вас на куски. Да, они защитят свою императрицу.'),
(62837, 10, 0, 'Слушайте меня, подданные. Чужаки угрожают вашей императрице. Они хотят убить меня, убить нас всех! Уничтожте их!'),
(62837, 11, 0, 'Закройте врата, перегородите выходы, уничтожьте чужаков.'),
(62837, 12, 0, 'Узнай вкус поражения!'),
(62837, 12, 1, 'Ты больше не будешь дышать!'),
(62837, 13, 0, 'Пой.,, да! Пой в агонии.'),
(62837, 14, 0, 'Теперь ты будешь служить МНЕ!');
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES
(63942, 0, 0, 'USELESS! Fall limp and die before me, wretched insect. Your kind are worthless to me!', 14, 0, 100, 0, 0, 32645, 'Sha of Fear (HoF) defeat 1'),
(63942, 1, 0, 'As for the rest of you, seek me if you dare. Like the others, you will all die screaming.', 14, 0, 100, 0, 0, 32646, 'Sha of Fear (HoF) defeat 2'),
(63942, 2, 0, 'No more excuses, Empress! Eliminate these cretins or I will kill you myself!', 14, 0, 100, 0, 0, 32647, 'Sha of Fear (HoF) defeat 3'),
(63942, 3, 0, 'You must destroy these outsiders, empress! They have come to conquer your people!', 14, 0, 100, 0, 0, 32648, 'Sha of Fear (HoF) recharge 1'),
(63942, 3, 1, 'The mantid are under MY command, fools. See how she succumbs to me.', 14, 0, 100, 0, 0, 32649, 'Sha of Fear (HoF) recharge 2');
INSERT INTO `locales_creature_text` (`entry`,`textGroup`,`id`,`text_loc8`) VALUES
(63942, 0, 0, 'Ничтожество, пади передо мной и умри. Жалкое насекомое, я презираю ваш род!'),
(63942, 1, 0, 'Ну а что до вас, ищите меня, если вам хватит смелости. Все равно вы умрете в муках, как и все остальные.'),
(63942, 2, 0, 'Больше никаких оправданий, императрица! Избавься от этих кретинов, или я сам убью тебя!'),
(63942, 3, 0, 'Ты должна уничтожить этих чужаков, императрица. Они пришли, чтобы поработить твой народ!'),
(63942, 3, 1, 'Богомолы подчиняются мне, глупцы. Смотрите, как она делает то, что я говорю.');