INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES
(61485, 0, 0, 'The temple will fall! You cannot stop my forces!', 14, 0, 100, 0, 0, 30277, 'Pavalak aggro'),
(61485, 3, 0, 'My army will crush you!', 14, 0, 100, 0, 0, 30278, 'Pavalak event 01'),
(61485, 2, 0, 'Niuzao\'s forces attack from the rear!', 14, 0, 100, 0, 0, 30279, 'Pavalak event 02'),
(61485, 3, 1, 'Stop them! Mantid attack!', 14, 0, 100, 0, 0, 30280, 'Pavalak event 03'),
(61485, 4, 0, '', 14, 0, 100, 0, 0, 30281, 'Pavalak event 04'),
(61485, 5, 0, '', 14, 0, 100, 0, 0, 30282, 'Pavalak event 05'),
(61485, 3, 2, 'Put down your weapons or die!', 14, 0, 100, 0, 0, 30283, 'Pavalak event 06'),
(61485, 1, 0, 'I have failed you, empress...', 14, 0, 100, 0, 0, 30284, 'Pavalak death'),
(61485, 6, 0, 'The empress demands it!', 14, 0, 100, 0, 0, 30285, 'Pavalak slay 1'),
(61485, 6, 1, 'The forces of Niuzao are weak!', 14, 0, 100, 0, 0, 30286, 'Pavalak slay 2');
INSERT INTO `locales_creature_text` (`entry`,`textGroup`,`id`,`text_loc8`) VALUES
(61485, 0, 0, 'Этот храм падет! Вам не остановить мою армию!'),
(61485, 1, 0, 'Я подвел вас, императрица...'),
(61485, 2, 0, 'Мои войска вас сокрушат!'),
(61485, 3, 0, 'Войска Нюцзао атакуют нас с тыла!'),
(61485, 2, 1, 'Остановить их! Богомолы, в атаку!'),
(61485, 4, 0, 'Пленники убегают!'),
(61485, 5, 0, 'Фланг! Атакуйте с фланга!'),
(61485, 2, 2, 'Сложите оружие или умрите!'),
(61485, 6, 0, 'Императрица требует этого!'),
(61485, 6, 1, 'Нюцзао слаб!');
